mapper.select:
SELECT COUNT(*) AS listCnt FROM member




			WHERE MID like #{keyword}


			WHERE MNAME like #{keyword}


			WHERE MBIRTH like #{keyword}

SELECT * 
	FROM (
		SELECT ROWNUM RN, A.* 
			FROM (
					SELECT * 
					FROM member 



							WHERE MID like #{keyword} 


							WHERE MNAME like #{keyword} 


							WHERE MBIRTH like #{keyword} 



					ORDER BY MNUM ASC 
					) A
			)

	WHERE RN BETWEEN #{startList} +1 AND #{startList} + #{listSize}
